// Place local declarations here.
clock t_resp;
id_v_t victim;
pos_t victimPos;

bool can_go_to(pos_t pos){
    return !isOccupied(pos) && map[pos.x][pos.y] != FIRE;
}

// Returns the ID of the closest entity, using the util function
int getClosest(int type) {
    return utilGetClosest(type, responders[id]);
}

// Returns the pos_t of the closest entity, using the util function
pos_t getClosestPos(int type) {
    return utilGetClosestPos(type, responders[id]);
}

void move() {    
    pos_t oldPos = responders[id];
    pos_t newPos;
    pos_delta_t posDelta;
    int k;
    pos_t temp_x;
    pos_t temp_y;
    pos_t closest_victim = getClosestPos(VICTIM);

    posDelta.x = 0;
    posDelta.y = 0;
    t_resp = 0;

    if(closest_victim == pos_out_of_map){ // all victims are safe
        return;
    }
    // get delta x
    if(oldPos.x < closest_victim.x){
        posDelta.x = 1;
    }
    else if(oldPos.x > closest_victim.x){
        posDelta.x = -1;
    }

    // get delta y
    if(oldPos.y < closest_victim.y){
        posDelta.y = 1;
    }
    else if(oldPos.y > closest_victim.y){
        posDelta.y = -1;
    }

    // get closer to the exit
    newPos = oldPos;
    temp_x.x = oldPos.x + posDelta.x;
    temp_x.y = oldPos.y;
    temp_y.x = oldPos.x;
    temp_y.y = oldPos.y + posDelta.y;
    if(posDelta.x != 0 && can_go_to(temp_x)){ // get closer on x if possible
        newPos = temp_x;
    }
    else if(posDelta.y != 0 && can_go_to(temp_y)){ // get closer on y if possible
        newPos = temp_y;
    }
    else { // try all directions
        for(k=0; k<4; k++) {
            posDelta = shifts[k];
            if (!positionInvalid(oldPos.x+posDelta.x, oldPos.y+posDelta.y)) {
                newPos.x = oldPos.x + posDelta.x;
                newPos.y = oldPos.y + posDelta.y;
            }
            if (can_go_to(newPos)) { 
                k=4;
            }
        }
    }
    if (can_go_to(newPos)) { 
        responders[id] = newPos;
    }   
    return;
}

void helpBeginningFar() {
    responders_busy[id] = true;
    victim = survToFRVictim[id];
    victimPos = victims[victim];
    t_resp = 0;
}
void helpBeginningNear() {
    responders_busy[id] = true;
    victim = getClosest(VICTIM);
    victimPos = victims[victim];
    t_resp = 0;
}

void doneHelping(){
    responders[id] = victimPos;
    t_resp = 0;
    responders_busy[id] = false;
}

void exitMap() {
    victims[id] = pos_out_of_map;
}

void setBusy() {
    victims_busy[id] = true;
}

// Place local declarations here.

clock t; 
pos_delta_t dir = {-1, -1};
int[0,5] Nv = 0;

bool hit_x_bound(){
    coordx_t min_x = bounds[id].min_x;
    coordx_t max_x = bounds[id].max_x;
    return (drones[id].x + dir.x < min_x) || (drones[id].x + dir.x > max_x);
}

bool hit_y_bound(){
    coordy_t min_y = bounds[id].min_y;
    coordy_t max_y = bounds[id].max_y;
    return (drones[id].y + dir.y < min_y) || (drones[id].y + dir.y > max_y);
}

bool on_track(){
    coordy_t min_y = bounds[id].min_y;
    return (drones[id].y - min_y) % (2 * Nv + 1) == 0;
}

void patrol() {
    coordx_t min_x = bounds[id].min_x;
    coordx_t max_x = bounds[id].max_x;
    coordy_t min_y = bounds[id].min_y;
    coordy_t max_y = bounds[id].max_y;

    Nv = drones_visibilities[id];
    if (hit_x_bound() && hit_y_bound()){ //hit both bounds ==> flip directions and restart
        dir.x = -dir.x;
        dir.y = -dir.y;
        if(!hit_x_bound()) {//if can move x do it
            drones[id].x += dir.x;
        }
        else if(!hit_y_bound()) { //if can move y do it
            drones[id].y += dir.y;
        }
        //else stand still
    }
    else if (!hit_x_bound()) { //if in bound move along x
        drones[id].x += dir.x;
    }
    else { // cannot move further x and not hit y bound => move y until new track or end
        drones[id].y += dir.y;
        if (on_track() && !hit_y_bound()) { //on the last y-move flip x direction
            dir.x = -dir.x;
        }
    }
}

// Returns the ID of the closest entity, using the util function
int getClosest(int type) {
    return utilGetClosest(type, drones[id]);
}

// Returns the pos_t of the closest entity, using the util function
pos_t getClosestPos(int type) {
    return utilGetClosestPos(type, drones[id]);
}

bool survivorNear() {
    if ( distance(drones[id], getClosestPos(SURVIVOR)) > Nv || distance(drones[id], getClosestPos(VICTIM)) > Nv ) {
        return false;
    }
    return true;
}

bool survivorWillHelp() {
    if ( distance(getClosestPos(SURVIVOR), getClosestPos(VICTIM)) < distance(getClosestPos(SURVIVOR), getClosestPos(FIRST_RESPONDER)) + distance(getClosestPos(FIRST_RESPONDER), getClosestPos(VICTIM)) )
        return true;
    return false;
}

