clock t;
id_v_t victim;
id_f_t responder;
pos_t victimPos;
pos_t responderPos;

bool can_go_to(pos_t pos){
    return  !isOccupied(pos) && !tileIsNearSomething(pos, FIRE) && map[pos.x][pos.y] != FIRE;
}

pos_t get_closest_exit(pos_t pos){
    coordx_t i;
    coordy_t j;
    int closestDist = INT16_MAX;
    pos_t closest_exit;
    pos_t temp;

    for(i=0; i < N; i++){
       for(j = 0; j < M; j++){
            temp.x = i;
            temp.y = j;
            if(map[i][j] == EXIT && distance(pos, temp) < closestDist){
                closest_exit = temp;
            }
        }
    }
    return closest_exit;
}

void move() {    
    pos_t oldPos = survivors[id];
    pos_t newPos;
    pos_delta_t posDelta;
    int k;
    pos_t temp_x;
    pos_t temp_y;
    pos_t closest_exit = get_closest_exit(oldPos);

    posDelta.x = 0;
    posDelta.y = 0;
    t = 0;

    // get delta x
    if(oldPos.x < closest_exit.x){
        posDelta.x = 1;
    }
    else if(oldPos.x > closest_exit.x){
        posDelta.x = -1;
    }

    // get delta y
    if(oldPos.y < closest_exit.y){
        posDelta.y = 1;
    }
    else if(oldPos.y > closest_exit.y){
        posDelta.y = -1;
    }

    // get closer to the exit
    newPos = oldPos;
    temp_x.x = oldPos.x + posDelta.x;
    temp_x.y = oldPos.y;
    temp_y.x = oldPos.x;
    temp_y.y = oldPos.y + posDelta.y;
    if(posDelta.x != 0 && can_go_to(temp_x)){ // get closer on x if possible
        newPos = temp_x;
    }
    else if(posDelta.y != 0 && can_go_to(temp_y)){ // get closer on y if possible
        newPos = temp_y;
    }
    else { // try all directions
        for(k=0; k<4; k++) {
            posDelta = shifts[k];
            if (!positionInvalid(oldPos.x+posDelta.x, oldPos.y+posDelta.y)) {
                newPos.x = oldPos.x + posDelta.x;
                newPos.y = oldPos.y + posDelta.y;
            }
            if (can_go_to(newPos)) { 
                k=4;
            }
        }
    }
    if (can_go_to(newPos)) {
        survivors[id] = newPos;
    }
    return;
}

